<?phpnamespace shop;class Router{    /**     * маршруты     * @var array     */    private static array $routes = [];    /**     * текущий маршрут     * @var array     */    private static array $route = [];    /**     * добавить маршрут     * @param string $regexp     * @param array $route     */    public static function addRoute(string $regexp, array $route = []): void    {        self::$routes[$regexp] = $route;    }    /**     * получить массив маршрутов     * @return array     */    public static function getRoutes(): array    {        return self::$routes;    }    /**     * получить текущий маршрут     * @return array     */    public static function getRoute(): array    {        return self::$route;    }    public static function dispatch(string $url)    {        if (self::matchRoute($url)) {            echo 'OK';        } else {            throw new \Exception('Проблема c URL');        }    }    /**     * поиск соответствующего маршрута     * @param string $url     * @return bool     */    public static function matchRoute(string $url): bool    {        return false;    }}